INSERT INTO `hanziexp` (`id`, `label`, `myexp`, `address`) VALUES
(1, '风', '①（名）空气流动的现象。 ②（名）借风力吹（使东西干燥或纯净）：晒干～净。 ③（名）借风力吹干的：～鸡｜～肉。 ④（名）像风那样快：～发｜雷厉～行。 ⑤（名）风气；风俗：节约成～｜移～易俗。 ⑥（名）景象：～景｜～光。 ⑦（名）态度：作～｜～度。 ⑧（名）（～儿）风声；消息：闻～而动｜千万别漏～｜刚听见一点～儿就来打听。 ⑨（名）传说的；没有确实根据的：～闻｜～言～语。 ⑩指民歌：采～。《诗经》里的《国风》；是古代十五国的。 中医指某些疾病：羊痫～｜鹅掌～。 （Fēnɡ）姓。 〈古〉又同“讽”fěnɡ。', 'http://hanyu.iciba.com/hanzi.swf?furl=b24891983d095e5d3c2edf5e3c996948'),
(2, '惠', '①（名）好处；恩惠：小恩小～｜施～于人｜受～于人。 ②（动）给人好处：互利互～｜平等互～。 ③（副）敬辞；用于对方对待自己的行动。～临｜～顾｜～存。 ④（Huì）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=227079db401853d9fcd8ab97a90ae4e9'),
(3, '清', '①（形）（液体或气体）纯净：水～见底。 ②（形）寂静：～静｜冷～。 ③（形）公正廉洁：～官｜～廉。 ④（形）清楚：说不～｜问～。 ⑤（形）单纯：～唱｜～茶。 ⑥（形）一点不留：～帐。 ⑦（动）（帐目）还清；结清：～欠｜帐已经～了。 ⑧（动）点验：～…～行李的件数。 ⑨（名）朝代；公元1616…1912；满族人爱新觉罗·努尔哈赤所建；初名后金；1636年改为清。1644年入关；定都北京。 ⑩（Qīnɡ）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=adcfe4db2100818bdf5b0f6dc958db70'),
(4, '气', '①（名）气体：毒～｜煤～｜沼～。 ②（名）特指空气：～压。 ③（名）指自然界冷热阴晴等现象：天～｜～候｜～象｜秋高～爽。 ④（名）气息：没～了｜上不来～。 ⑤（名）味：香～｜臭～。 ⑥（名）人的精神状态：勇～｜朝～。 ⑦（名）人的作风习气：官～｜娇～。 ⑧（动）生气；发怒：～人。 ⑨（动）使人生气：你别～我。（名）欺压：再也不受资本家的～了。（名）中医指人体内能使各器官正常地发挥机能的原动力：元～。（名）中医指某种病象：湿～。 见“气”。', 'http://hanyu.iciba.com/hanzi.swf?furl=3d3b3453c7e03aa436d7f9d79d3645cd'),
(5, '朗', '①本义：明朗。（形）光线充足；明亮：明～｜晴～｜豁然开～｜天～气清。 ②本义：明朗。（形）声音清晰响亮：～诵｜～读。', 'http://hanyu.iciba.com/hanzi.swf?furl=774af26773042361ab8445cf795bed5b'),
(6, '天', '①（名）头顶。 ②（名）天空：～边。 ③（名）顶部的；凌空架设的：～窗。 ④（名）一昼夜二十四小时的时间；有时专指白天：今～。 ⑤（名）一天的某一段时间：天不早啦。 ⑥（名）季节：春～｜三伏～。 ⑦（名）天气：阴～。 ⑧（名）天然的；天生的：～资｜～才。 ⑨（名）自然界：～灾。迷信的人指自然界的主宰者；造物者：～意。迷信的人指神佛仙人所住的地方：～堂｜归～。', 'http://hanyu.iciba.com/hanzi.swf?furl=249aba763258bbe488af3e79a381d265'),
(7, '也', '①（助）〈书〉表示判断或解释的语气：孔子；鲁人～。 ②（助）〈书〉表示疑问或反对的语气：何～？｜是可忍；孰不可忍～？ ③（助）〈书〉表示句中的停顿：不道之行～；天下为公。 ①（副）同样：水库可以发电；～可以养鱼。 ②（副）叠用；强调两事并列式对待：他～会种地；～会打铁。 ③（副）叠用；表示无论这样或那样；不以某种情形为条件：你去我～去；你不去我～不去。 ④（副）表示转折或让步（常跟上文的‘虽然、即使’等呼应）：我虽然没看过；～听人说过｜即使你不说；我～知道。 ⑤（副）表示委婉：倒～罢了｜～只好如此。 ⑥（副）表示强调（常跟上文的‘连’字呼应）：连爷爷～乐得合不拢嘴。', 'http://hanyu.iciba.com/hanzi.swf?furl=61faca1caedf304161bcae999ade2f7b'),
(8, '日', '①（名）太阳：～出｜～落。 ②（名）指日本：～圆｜～语。 ③（名）从天亮到天黑的一段时间；白天：～班｜～～夜夜。 ④（名）地球自转一周的时间；天：今～｜改～再见。 ⑤（名）每天；一天一天地：～记｜～新月异。 ⑥（名）泛指一段时间：往～｜来～。 ⑦（名）特指某一天：生～｜国庆～。', 'http://hanyu.iciba.com/hanzi.swf?furl=3edddd85ac2c5e612fb71dbb89e7d1c5'),
(9, '是', '① ②（动）对；正确：一无～处。 ③〈书〉（动）认为正确：～古非今。 ④（名）表示答应的词。 ⑤姓。〈书〉（代）这；这个：～可忍；孰不可忍？ ⑥（动）联系两种事物；表明两者同一或者后者说明前者事物的种类、属性：《阿Q正传》的作者～鲁迅。 ⑦（动）与“的”字相应；有分类的作用：这张桌子～石头的。 ⑧（动）联系两种事物；表示陈述的对象属于“是”后面所说的情况：他～一片好心。 ⑨（动）表示存在；主语通常是表处所的语词；“是”后面表示存在的事物：院子里种的～花。 ⑩（副）“是”前后用相同的名词或动词；连用两个这样的格式；表示所说的几个事物互不相干；互不混淆：说～说；做～做；有意见也不能耽误干活儿。 （副）在上半句里“是”后用相同的名词、形容词或动词；表示让步；含有“虽然”的意思：东西旧～旧；可是还能用。 （助）用在句首；加重语气：～谁告诉你的？ （副）用在名词前面；含有“凡是”的意思：～有利于群众的事情他都肯干。 （助）用在名词前面；含有“适合”的意思：这场雨下的～时候。（副）用在选择问句、是非问句或反问句里：你～吃米饭～吃面？（助）（必须重读）表示坚决肯定；含有“的确、实在”的意思：我打听清楚了；他那天～没去。 古同“是”。', 'http://hanyu.iciba.com/hanzi.swf?furl=0a60ac8f02ccd2cf723f927284877851'),
(10, '盛', '①（动）把东西放在器具里。 ②（动）容纳。另见shènɡ。 ①（形）兴盛；繁盛：全～时期。 ②（形）强烈；旺盛：年轻气～。 ③（形）盛大；隆重：～会。 ④（形）深厚：～情。 ⑤（形）普遍；广泛：～行。 ⑥（形）用力大；程度深：～赞。 ⑦（形）（名）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=7c7fedd2c42470f3420e6325f2ae1895'),
(11, '之', '〈书〉本义：（动）往：由京～沪。 ①〈书〉（代）代替人或事物（限于做宾语）：取而代～｜反其道而行～。 ②〈书〉（代）虚用；无所指：久而久～｜手之舞～；足之蹈～。 ③〈书〉（代）这；那：～二虫｜～子于钓。 ①〈书〉（助）用在定语和中心词之间；组成偏正词组。a）表示领属关系：赤子～心｜钟鼓～声｜以子～矛；攻子～盾。b）表示一般的修饰关系：光荣～家｜无价～宝｜缓兵～计｜千里～外｜意料～中｜十分～九。 ②〈书〉（助）用在主谓结构之间；取消它的独立性；使变成偏正结构：中国～大｜战斗～烈｜大道～行也；天下为公。', 'http://hanyu.iciba.com/hanzi.swf?furl=0ff60e34aa8bcfb224af5d6d280a058c'),
(12, '频', '（副）屡次；连续几次：捷报～传｜～～点头。', 'http://hanyu.iciba.com/hanzi.swf?furl=b4050f71aadecade778fcc91660b86ed'),
(13, '品', '①（名）物品：商～｜产～｜战利～。 ②（量）等级；品级：上～｜下～。 ③（量）种类：～种。 ④（名）品质：人～｜～德。 ⑤（动）辨别好坏；品评：～茶。 ⑥（动）吹（管乐器；多指箫）：～箫｜～竹弹丝。 ⑦（Pǐn）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=49aa6d7db5bca84ea6d7a7470b13c81b'),
(14, '察', '（动）仔细看；调查：观～｜考～｜～其言；观其行。', 'http://hanyu.iciba.com/hanzi.swf?furl=3077abd6191b5041dd702f32aed68e2c'),
(15, '俯', '①（动）（头）低下（跟‘仰’相对　）：～首｜～视｜～冲。 ②（名）敬辞；旧时公文书信中用来称对方的动作：～允｜～念。', 'http://hanyu.iciba.com/hanzi.swf?furl=0096e2a37079afa04ccdecc9244c3b4d'),
(16, '大', '①（形）在体积、面积、数量、力量、强度等方面超过所比较的对象：这张桌子比那张～。 ②（形）大小：你多～了。　 ③（副）程度深：～吃一惊。 ④（副）用在“不”前或后表示程度浅或程度深：不～爱说话；～不相同。 ⑤（形）排行第一的：在家里我老～。 ⑥（形）敬辞；称与对方有关的事物：尊姓～名。 ⑦（形）用在时令或节日前；表示强调：～热天。 ⑧姓。 ⑨（名）〈方〉父亲：俺～。（名）〈方〉伯父或叔父：三～到我家来了。（形）〈古〉同“太”、“泰”（tài）；如大子。 见“大夫”、“大王”。', 'http://hanyu.iciba.com/hanzi.swf?furl=ab18e30c0dc4093ceeda8961fac3d1f4'),
(17, '宙', '（名）指古往今来所有的时间：宇～。', 'http://hanyu.iciba.com/hanzi.swf?furl=7d77ec6c1d12703a5750bf54b043edb4'),
(18, '宇', '①（名）房檐；泛指房屋：屋～｜栋～。 ②（名）上下四方；所有的空间；世界：～宙｜～内。', 'http://hanyu.iciba.com/hanzi.swf?furl=987e8f1de473c8c6b17e0adcedfa5b4c'),
(19, '观', '①（动）看：走马～花｜坐井～天。 ②（名）景象或样子：奇～｜改～。 ③（名）对事物的认识或看法：乐～｜悲～｜世界～。 ①（名）道教的庙宇：道～。 ②（Guàn）姓。另见ɡuān。', 'http://hanyu.iciba.com/hanzi.swf?furl=0befc61f973a7b60afa0d29eb677c0fb'),
(20, '听', '①（动）本义：用耳朵接受声音：～见｜～写。 ②（动）听从（劝告）；接受（意见）：～从｜～信。 ③（名）治理；判断：～政｜～讼。 ④（动）听凭；任凭：～其自然。 ⑤（名）〈方〉铁皮做的装食品、香烟等的罐子：一～香烟｜～装。 见“听”。 古同“听”。', 'http://hanyu.iciba.com/hanzi.swf?furl=8cbe1c93ad66a95e65967982465b3064'),
(21, '视', '①（动）看：～力｜近～。 ②（动）看待：轻～。 ③（动）考察：巡～。 ①古同“视”。 ②语。 见“视”。', 'http://hanyu.iciba.com/hanzi.swf?furl=9709aaca3267cb96944eda52f93a77f9'),
(22, '极', '①（名）顶点；尽头：登峰造～｜无所不用其～（用尽可能使用的各种手段）。 ②（名）地球的南北两端；磁体的两端；电源或电器上电流进入或流出的一端：南～｜北～｜阴～｜阳～。 ③（动）尽；达到顶点；～力｜～目四望｜物～必反｜～一时之盛。 ④（形）最终的；最高的：～度｜～端｜～量。 ⑤（副）表示达到最高度：～重要｜～少数。注意：“极”也作补语；但前头不能用‘得’；后面一般带‘了’；如‘忙极了’。 见“极”。', 'http://hanyu.iciba.com/hanzi.swf?furl=5cc088c0b5f788988353d609ecb09a24'),
(23, '足', '①（名）脚；腿：～迹｜～球｜画蛇添～。 ②（名）器物下部形状像腿的部分：鼎～。 ①（形）充足；足够：～额｜～金｜～数｜～月｜富～｜丰衣～食。 ②（副）够得上某种数量或程度：菜～有十斤。 ③（副）足以（多用于否定式）：不～为凭｜微不～道。', 'http://hanyu.iciba.com/hanzi.swf?furl=fbb4674dab3f2f7c814e58abde1f4138'),
(24, '怀', '①（名）胸部或胸前：掩着～｜小孩在妈妈～里。 ②（名）心怀；胸怀：壮～｜襟～｜正中下～。 ③（动）思念；想念：～乡｜～友｜～古。 ④（动）腹中有（胎）：～胎｜～孕。 ⑤（动）心理存有：～恨｜不～好意｜～有大志。 ⑥（Huái）姓。 见“怀”。', 'http://hanyu.iciba.com/hanzi.swf?furl=29d035535f1a94f7103b52027a1bd59a'),
(25, '骋', '①（动）〈书〉跑。 ②（动）〈书〉放开。 见“骋”。', 'http://hanyu.iciba.com/hanzi.swf?furl=7d62160eccc5bf0193052c0ddc0804e9'),
(26, '目', '①（名）眼睛：有～共睹｜历历在～。 ②（动）〈书〉看：～为奇迹。 ③（名）大项中再分的小项：项～｜细～｜纲举～张。 ④（名）目录：书～｜药～｜剧～。 ⑤（名）生物学中把同一纲的生物按照彼此相似的特征分为若干群；每一群叫一目；如鸟纲中有雁形目、鸡形目等。目以下为科。', 'http://hanyu.iciba.com/hanzi.swf?furl=4064560fbd2f8f6ae7723a0a4e77eab8'),
(27, '游', '①（动）基本义：人或动物在水里行动：～水｜～艇｜～泳。 ②（动）从容地行走；闲逛：～逛｜～街｜～览｜～乐｜～历｜～憩｜～玩｜～兴。 ③（动）〈书〉交游；来往。 ④（动）不固定的；经常移动的：～牧｜～民｜～击｜～好｜～刃有余｜～手好闲｜浮～｜浪～｜远～｜云～。 ⑤（形）江河的一段：上～｜中～｜下～。 ⑥（Yóu）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=e34cb018b232d8d992b7dfcf8e6b9150'),
(28, '所', '①（名）处所。 ②（名）明代驻兵的地点；大的叫千户所；小的叫百户所。现在只用于地名：海阳～（在山东）｜前～（在浙江）。 ③（名）用做机关或其它办事地方的名称：科研～｜管理～。 ④（量）ɑ）用于房屋：两～房子。b）用于学校等（可以不止一所房子）：一～学校。 ⑤（助）ɑ）跟“为”或“被”合用；表示被动：为人～笑。b）用在做定语的主谓结构的动词前面；表示中心词是受事者：我～知道的事。c）用在“是……的”中间的名词、代词和动词之间；强调施事者和动作的关系：高考；是家长～关心的事。d）〈书〉用在动词前面；跟动词构成介词结构：～做｜～想。', 'http://hanyu.iciba.com/hanzi.swf?furl=1def10bd27b54e5f2b56b37c418ae363'),
(29, '与', '同“欤”；古汉语助词；表示疑问；用法跟“乎”基本相同。 ①（动）给：赠～｜～人方便。 ②（动）交往；友好：相～｜～国（友邦）。 ③（动）赞许；赞助：～人为善。 ①（介）跟：～虎谋皮｜～困难作斗争。 ②（连）和：工业～农业。 （动）参与。', 'http://hanyu.iciba.com/hanzi.swf?furl=3b04598bd00dd36669cd0c86fb2c15b4'),
(30, '相', '①（副）表示动作行为上的关联：互～｜～像｜～让｜～距太远。 ②（副）表示一方对另一方的动作：实不～瞒｜好言～劝。 ③（xiānɡ）姓。 （动）亲自观看（是不是合心意）：～亲｜～看｜～中。 ①（名）（～儿）相貌；外貌：长～｜聪明～｜狼狈～。 ②（名）物体外观：月～｜金～。 ③（名）坐、立等的姿态：站有站～；坐有坐～。 ④（动）观察事物的外表；判断其优劣：～马。 ⑤（ ⑥iànɡ）姓。 ①（动）辅助：吉人天～（套语；用来安慰遭遇危险或困难的人）。 ②（名）宰相：丞～。 ③（名）某些国家的官名；相当于中央政府的部长。 ④（名）旧时指帮助主人接待宾客的人：傧～。', 'http://hanyu.iciba.com/hanzi.swf?furl=052be3fb2c687fbf707d01dde49b57f3'),
(31, '人', '①（名）能制造工具并使用工具进行劳动的高等动物：男～｜女～｜～们｜～类。 ②（名）每人；一般人：～手一册｜～所共知。 ③（名）指成年人：长大成～。 ④（名）指某种人：工～｜军～｜主～｜介绍～。 ⑤（名）别人：～云亦云｜待～诚恳。 ⑥（名）指人的品质、性格或名誉：这个同志～很好｜他～老实。 ⑦（名）指人的身体或意识：这两天～不大舒服。 ⑧（名）指人手、人材：我们这里正缺～。', 'http://hanyu.iciba.com/hanzi.swf?furl=465afe3c118589de357745a709c0441f'),
(32, '夫', '①（名）丈夫：～妻｜～妇｜姐～｜姑～。 ②（名）成年男子：匹～｜一～当关；万～莫开。 ③（名）称从事某种体力劳动的人：渔～｜农～｜轿～。 ④（名）旧时称服劳役的人；特指被统治阶级强迫去做苦工的人：～役｜拉～。 （名）旧时称服劳役的人。 ①〈书〉 ②文言代词：～天地者。 ③文言助词：逝者如斯～。', 'http://hanyu.iciba.com/hanzi.swf?furl=000478ee58a709ba7adddb010459a970'),
(33, '乐', '①（形）快乐：欢～｜～事｜～不可支。 ②（动）乐于：～此不疲。 ③（动）〈口〉笑：逗～。 ④（Lè）姓（与Yuè不同姓）。 ①（名）音乐。 ②（名）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=a2705d7fc85fec8eec1211649ce2d3ca'),
(34, '可', '①（动）表示同意：许～｜认～｜不置～否。 ②（动）表示许可或可能；跟“可以”的意思相同（限于熟语或正反对举）：由此～见｜～大～小。 ③（副）表示值得：～贵。 ④（副）〈书〉大约：年～二十。 ⑤（Kě）姓。 ①副词；表示转折；意思跟“可是”相同：别看他年龄小；志气～不小。 ②表示强调：她待人～好了。 ③用在反问句里强调反问的语气：都这样说；～谁见过呢？ ④用在疑问句里加强疑问的语气：你～曾跟他谈过这个问题？ （动）适合：～人意。', 'http://hanyu.iciba.com/hanzi.swf?furl=4aebcc5f1c6d906aac2fc13f01190c6c'),
(35, '信', '①（副）确实：～史｜～而有证。 ②（名）信用：守～｜失～｜言而有～。 ③（动）相信：～托｜～任。 ④（动）信奉（宗教）：～教｜～徒。 ⑤（动）听凭；随意；放任：～步｜～口开河。 ⑥（名）凭据：～号｜～物。 ⑦（动）按照习惯的格式把要说的话写下来给指定的对象看的东西；书信：介绍～｜证明～。 ⑧（名）（～儿）信息：音～｜口～儿。 ⑨（名）引信：～管。同“芯”（xìn）。姓。 （名）信石；即砒霜；因产地信州（今江西上饶一带）得名：红～｜白～。', 'http://hanyu.iciba.com/hanzi.swf?furl=bf94136271a65872ea73052d4ab9837b'),
(36, '娱', '①（动）使快乐：聊以自～｜文～。 ②（形）快乐：～乐｜欢～。', 'http://hanyu.iciba.com/hanzi.swf?furl=52c0d7f1c807b55a1591302edb000b2c'),
(37, '内', '①（形）里头；里头的（跟‘外’相对）：～衣｜～部｜国～｜室～。 ②（形）指妻或妻的亲属：～人｜～侄｜～弟。 ③（形）〈古〉又同“纳”nà。', 'http://hanyu.iciba.com/hanzi.swf?furl=45a5242566834545b0b0cfb71f82df78'),
(38, '室', '①（名）屋子：教～｜卧～。 ②（名）机关、乡镇、工厂、学校等内部的工作单位：档案～。 ③（名）妻子：继～。 ④（名）二十八宿之一。', 'http://hanyu.iciba.com/hanzi.swf?furl=9d730376f5b171960de9977a0f9ef2d0'),
(39, '一', '①（数）数目；最小的正整数。 ②（形）同一：～视同仁｜咱们是～家人｜咱们～路走｜这不是～码事。 ③（形）另一：番茄～名西红柿。 ④（形）全；满：～冬｜～生｜～路平安｜～屋子人｜～身的汗。 ⑤（形）专一：～心～意。 ⑥（副）表示动作是一次或表示动作是短暂的；或表示动作是试试的。a）用在重叠的动词（多为单音）中间：歇～歇｜笑～笑。b）用在动词之后；动量词之前：笑～声｜看～眼。 ⑦（副）用在动词或动量词前面；表示先做某个动作（下文说明结果）：～跳跳了过去｜～脚把它踢开。 ⑧（副）一旦；一经：～失足成千古恨。 ⑨（助）〈书〉用在某些词前加强语气：～何速也｜为害之甚；～至于此！ （名）我国民族音乐音阶上的一级；乐谱上用做记音符号。', 'http://hanyu.iciba.com/hanzi.swf?furl=7941da94db2a83c04d0d85ee45ecb1e3'),
(40, '言', '①（名）话：～语｜语～｜格～。 ②（动）说：～之有理｜畅所欲～。 ③（量）汉语的一个字叫一言：五～诗。 ④姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=098a366cd1395d71fa0bb04194143d3a'),
(41, '悟', '（动）理解；明白；了解；领会；觉醒：～出｜～到。', 'http://hanyu.iciba.com/hanzi.swf?furl=14740b11d47bb11092fa87bc1deb9103'),
(42, '抱', '①本义：（动）用手臂围住：～孩子。 ②（动）初次得到（儿子或孙子）。 ③（动）领养（孩子）。 ④（动）〈方〉结合在一起：～成团。 ⑤（动）〈方〉（衣、鞋）大小合适：这双鞋～脚儿。 ⑥（动）心里存着（想法、意见）：青年人都～着远大的革命理想。 ⑦（量）表示两臂合围的量：一～草。', 'http://hanyu.iciba.com/hanzi.swf?furl=1075ddb29c67dbfa665ce5144abf0c9a'),
(43, '诸', '①（形）众；许多：～公｜～侯｜～　君｜～位｜～子百家。 ②姓。 （助）〈书〉“之于（於）”或“之乎”的合音：付～实施（=之于）｜公～社会（=之于）｜有～？（=之乎） 见“诸”。', 'http://hanyu.iciba.com/hanzi.swf?furl=a9d31235c0de7c498e58687e56ec8e9b'),
(44, '取', '（动）选取：～道｜录～｜可～。', 'http://hanyu.iciba.com/hanzi.swf?furl=55c3f66036f9e62ba6afa7d8e2d1976c'),
(45, '或', '①（副）或许；也许：慰问团已经起程；明日上午～可到达。 ②（连）或者：～多～少｜不解决桥～船的问题；过河就是一句空话。 ③（名）〈书〉某人；有的人：～告之曰。 ④（副）〈书〉稍微：不可～缓。', 'http://hanyu.iciba.com/hanzi.swf?furl=a7185263988794ecaef531dde9f3a1e4'),
(46, '世', '①（名）人的一辈子：一生一～． ②（名）有血统关系的人相传而成的辈分：第十～孙。 ③（名）一代又一代：三代祖传～医。 ④（名）指有世交关系：～兄。 ⑤（名）时代：近～｜当～。 ⑥（名）社会；人间：问～｜～道。 ⑦（名）姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=ec9a6f6b0900b1d5b816ec97d0f5a673'),
(47, '虽', '①本义：虫；像蜥蜴而大。〈书〉（连） ②虽然。 ③纵然。 见“虽”。', 'http://hanyu.iciba.com/hanzi.swf?furl=5c04aa43e6d54df6196d853e6466bb39'),
(48, '骸', '①（名）骸骨。 ②（名）借指身体。', 'http://hanyu.iciba.com/hanzi.swf?furl=b1f774f802a735f8b18723eb5684aad5'),
(49, '形', '①（名）形状：方～｜图～｜地～。 ②（名）形体；实体：有～｜～影不离。 ③（动）显露；表现：喜～于色｜～诸笔墨。 ④（动）对照：相～见绌｜相～之下。', 'http://hanyu.iciba.com/hanzi.swf?furl=04fa9412bb42a880eaffbb7af6858525'),
(50, '浪', '①（名）本义：大波。（名）波浪：风平～静｜乘风破～。 ②（名）本义：大波。（名）像波浪起伏的东西：麦～｜声～。 ③（名）本义：大波。（动）没有约束；放纵：放～｜～费。 ④（名）本义：大波。（动）〈方〉逛。', 'http://hanyu.iciba.com/hanzi.swf?furl=e15e41f2d5662defc0cfda2bef884281'),
(51, '放', '①（动）解脱约束：释～。 ②（动）任意；随便：～任。 ③（动）发出：～光。 ④（动）扩展：～大。 ⑤（动）搁；置：～在桌子上。 ⑥（动）古指把人驱逐到边远的地方：流～。', 'http://hanyu.iciba.com/hanzi.swf?furl=cedaabc91c5cb7249a74a4ff89f4dce3'),
(52, '托', '①（动）本义：寄托：～儿所｜～孤。 ②（动）手掌或其他东西向上承受（物体）：～起｜～盘。 ③（名）（托儿）托子；类似托子的东西：花～。 ④陪衬：烘～｜衬～。 ⑤（动）委托；寄托：拜～｜恳～。 ⑥（动）推托。 ⑦（动）依赖。 ⑧（名）压强单位；一托等于一毫米汞柱的压强；一大气压为760托。旧作乇（tuō）。 见“讬”。', 'http://hanyu.iciba.com/hanzi.swf?furl=0cdd63e6de7090439acfce3fd8e8ef67'),
(53, '寄', '①（动）原指托人递送；现在专指通过邮局递送：～信｜～钱｜～包裹。 ②（动）付托；寄托：～存｜～希望于青年。 ③（动）依附别人；依附别的地方：～食｜～居。 ④（动）认的（亲属）：～父｜～母｜～儿｜～女。', 'http://hanyu.iciba.com/hanzi.swf?furl=a861160989019e85bce6d0e1175a6b0b'),
(54, '因', '①（动）〈书〉沿袭：～循。 ②（介）〈书〉凭借；根据：～势利导｜～陋就简｜～地制宜｜～人成事。 ③（名）原因（跟‘果’相对）：～由｜事出有～｜前～后果。 ④（连）因为：～病请假｜会议～故改期。 古同“因”。╇', 'http://hanyu.iciba.com/hanzi.swf?furl=be6616c7f688b96475fd7c9e5ffa610a'),
(55, '欣', '（形）喜悦：～然｜～悦｜欢～。', 'http://hanyu.iciba.com/hanzi.swf?furl=fe10e18042e75517e950ad42ebaf321a'),
(56, '其', '用于人名；郦食（yì）其；汉朝人。 ①（代）他（她、它）的；他（她、它）们的。 ②（代）他（她、它）；他（她、它）们。 ③（代）那个；那样。 ④（代）虚指：忘～所以。〈书〉（助） ⑤（代）表示揣测、反诘：岂～然乎？｜～奈我何？ ⑥（代）表示命令：子～勉之！词尾：尤～｜极～｜如～｜大概～。', 'http://hanyu.iciba.com/hanzi.swf?furl=43608b6b5fc979888a0cc09f94e61a1a'),
(57, '当', '①（动）作；担任：他～班长。 ②（动）承担：敢作敢～。 ③（动）主持：～家。 ④（介）面对；面向：～众。 ⑤（动）相称：旗鼓相～。 ⑥（介）正在（那时候、那地方）：～场。 ⑦象声词：丁丁～～。 ①（动）合适：处理得～。 ②（动）抵得上；等于：以一～十。 ③（副）以为；认为：我～你走了。 ④（代）指同一时间：～年。 ⑤（动）旧社会用实物作抵押向当铺借钱：～钱。 作为人称的附缀 见“当”。 ①《廣韻》丁浪切，去宕，端。 ②见“家儅”，亦作“家儅”。 ①《廣韻》都郎切，平唐，端。 ②伴当。指随从的仆人。', 'http://hanyu.iciba.com/hanzi.swf?furl=249e061b3514db436457645891b382b2'),
(58, '同', '①（形）相同；一样：～样。 ②（介）跟…相～。 ③（副）共同；一　齐从事。 ④（介）引进动作时的对象；跟“跟”相同。 ⑤（介）引进比较的事物；跟“跟”相同。 ⑥（介）表示替人做事；跟“给”相同。 ⑦（连）表示联合关系；跟“和”相同。 ⑧（Tónɡ）姓。 （名） 〔衚～〕见“衚”。', 'http://hanyu.iciba.com/hanzi.swf?furl=49cd6e0ab7c4060390f2cc3a555aebc5'),
(59, '不', '①（副）用在动词、形容词和其他副词前面表示否定：～去｜～能。 ②（副）加在名词或名词性词素前面；构成形容词：～法｜～轨。 ③（副）单用；做否定性的回答（答话的意思跟问题相反）：他知道吧？～；他不知道。 ④（副）〈方〉用在句末表示疑问；跟反复问句的作用相等：他现在身体好～？ ⑤（副）用在动补结构中间；表示不可能达到某种结果：拿～动｜做～好。 ⑥（副）“不”字的前后叠用相同的名词；表示不在乎或不相干（常在前边加‘什么’）：什么边疆～边疆；只要工作需要；去哪儿都成。 ⑦（副）跟“就”搭用；表示选择：晚上他～是看书；就是看报。 ⑧（副）〈方〉不用；不要：～谢｜～送。', 'http://hanyu.iciba.com/hanzi.swf?furl=9eba1ab69790dcef9441935205d9e66d'),
(60, '躁', '（形）急躁；性急；不冷静：烦～｜急～｜不骄不～｜这人性子太～。', 'http://hanyu.iciba.com/hanzi.swf?furl=9668d415f3a3bfca8b88e0b0115a174a'),
(61, '静', '①停止的，与“动”相对：～止。～态。～物。平～。风平浪～。 ②没有声音：安～。寂～。僻～。冷～。肃～。～悄悄。～穆。～谧。～默。～观。～听。 ③安详，闲雅：～心。～坐。 ④古同“净”，清洁。 ⑤姓。', 'http://hanyu.iciba.com/hanzi.swf?furl=861a7657ed4d03b43abe6bc5c6f2bd2d'),
(62, '殊', '①（动）本义：死。 ②（形）不同；差异：～途同归。 ③（形）特别；特殊：～功｜～效。 ④（副）〈书〉很；极：悬～。 ⑤〈书〉（动）断；绝。', 'http://hanyu.iciba.com/hanzi.swf?furl=1728965e8cb64398c982f4d3fd769775'),
(63, '万', '（名）万俟；姓。 ①（量）基本义：数目；十个千、一万、万个。 ②（形）比喻很多：～代｜～家灯火。 ③（副）极；很；绝对：～不得已｜～～不能。 ④姓。 见“万”。', 'http://hanyu.iciba.com/hanzi.swf?furl=9d03206671827d3678b69b71b3dd9338'),
(64, '舍', '①（动）舍弃：四～五入。 ②（动）施舍：～药。 ①（名）房屋：校～。 ②（名）舍间：寒～。 ③（名）养家畜的圈：牛～。 ④（名）谦辞；用于对别人称自己的辈分低或年纪小的亲属：～侄｜～弟。 ⑤（名）姓。（量）古代三十里为一舍：退避三～。 见“舍2”。', 'http://hanyu.iciba.com/hanzi.swf?furl=1ad9bc444a6353c8161b850b5d7a5677'),
(65, '趣', '①（名）（～儿）趣味；兴趣：活泼有～｜自讨没～｜相映成～。 ②（名）有趣味的：～事｜～闻。 ③（名）志趣：异～（志趣不同）。〈古〉又同促（cù）。', 'http://hanyu.iciba.com/hanzi.swf?furl=e30c283f53b064eb4bd15b2db7f08037'),
(66, '自', '①（代）自己：～爱｜～备｜～便｜～费｜～供｜～家｜～荐｜～救｜～勉｜～弃｜～谦｜～强｜～杀｜～述｜～诉｜～习｜～信｜～省｜～许｜～选｜～学｜～专｜～足｜～不量力｜～掘坟墓｜～鸣清高｜～投罗网｜～我批评｜～言～语｜～作聪明。 ②（副）自然；当然：～不待言｜～当努力｜～有公道。 （介）从；由：～古｜～外｜～小｜～远而近。', 'http://hanyu.iciba.com/hanzi.swf?furl=fc051a9f8948a5da9ed154315e01165f'),
(67, '然', '①（形）对；不错：不以为～。 ②（代）如此；这样；那样：不尽～｜知其～；不知其所以～。 ③（连）〈书〉然而：事情虽小；～亦不可忽视。 ④副词或形容词后缀：忽～｜突～｜显～｜欣～｜飘飘～。〈古〉又同“燃”。', 'http://hanyu.iciba.com/hanzi.swf?furl=21d3e9c69ec3dea2cd23add3ddede023'),
(68, '快', '①（形）基本义：速度高；费的时间短。 ②（形）赶快；从速：～来帮忙｜大个～上。 ③（形）快要；将要：天～亮了｜他～四十岁了。 ④（形）灵敏：脑子～｜眼疾手～。 ⑤（形）锋利（跟‘钝’相对）：菜刀磨～了。 ⑥（形）爽快；痛快；直截了当：～人～语。 ⑦（形）愉快；高兴：～快｜拍手称～｜大～人心。', 'http://hanyu.iciba.com/hanzi.swf?furl=8fcedbfdde8f3f363eeeebac783a2e15'),
